package cn.bjjoy.bms.setting.controller;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Description;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.CrossOrigin;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.ResponseBody;import cn.bjjoy.bms.exception.ControllerException;import cn.bjjoy.bms.setting.entity.CamacAnswer;import cn.bjjoy.bms.setting.service.CamacAnswerService;import cn.bjjoy.bms.util.MapUtils;import cn.bjjoy.bms.util.Page;import cn.bjjoy.bms.util.PageUtils;import cn.bjjoy.bms.util.Response;import cn.bjjoy.bms.util.StringUtils;/** * 类描述   : CAMAC_回答 * 创建人	：system * 创建时间 ：2018-09-12 16:16:56 * @version 1.0 */@Controller@CrossOrigin@RequestMapping("/answer")public class CamacAnswerController{	@Autowired	private CamacAnswerService camacAnswerService;		@Description("CAMAC_回答-添加")	@RequestMapping(value="camac/answer.save")	public Response save(CamacAnswer camacAnswer) throws ControllerException {		camacAnswerService.save(camacAnswer);		return Response.success(camacAnswer.getAnswerId());	}		@Description("CAMAC_回答-删除")	@RequestMapping(value="camac/answer.delete")	public Response delete(String answerId) throws ControllerException {		if(StringUtils.isEmpty(answerId))			return Response.error("answerId不能为空");		camacAnswerService.deleteOne(answerId);		return Response.success();	}	@Description("CAMAC_回答-根据主键查询")	@RequestMapping(value="camac/answer.one")	public Response one(String answerId) throws ControllerException {		if(StringUtils.isEmpty(answerId))			return Response.error("answerId不能为空");		CamacAnswer camacAnswer = camacAnswerService.queryOne(answerId);		return Response.success(camacAnswer);	}		@Description("CAMAC_回答-查询列表")	@RequestMapping(value="camac/answer.list")	public Response list(@RequestParam Map<String, Object> map) throws ControllerException {		boolean pageOK = PageUtils.isOK(map);				if(!pageOK)			return Response.error("分页参数错误");				long total = camacAnswerService.queryMapListCount(map);				Page page = PageUtils.initPage(map, total);				List<Map<String,Object>> list = camacAnswerService.queryMapList(map);		return Response.success(list, page);	}		@Description("CAMAC_回答-根据主键修改")	@RequestMapping(value="camac/answer.update")	public Response update(@RequestParam Map<String, Object> map) throws ControllerException {		if(MapUtils.isEmpty(map, "answerId"))			return Response.error("answerId不能为空");		camacAnswerService.updateOne(map);		return Response.success();	}		@ResponseBody	@RequestMapping(value="/update")	public String update2() {		return "OK";	}}