package cn.bjjoy.bms.setting.controller;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Description;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.CrossOrigin;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import cn.bjjoy.bms.exception.ControllerException;import cn.bjjoy.bms.setting.entity.CamacBusiCatalog;import cn.bjjoy.bms.setting.service.CamacBusiCatalogService;import cn.bjjoy.bms.util.MapUtils;import cn.bjjoy.bms.util.Page;import cn.bjjoy.bms.util.PageUtils;import cn.bjjoy.bms.util.Response;import cn.bjjoy.bms.util.StringUtils;/** * 类描述   : CAMAC_产品目录 * 创建人	：system * 创建时间 ：2018-09-12 16:16:56 * @version 1.0 */@Controller@CrossOriginpublic class CamacBusiCatalogController extends BaseController {	@Autowired	private CamacBusiCatalogService camacBusiCatalogService;		@Description("CAMAC_产品目录-添加")	@RequestMapping(value="camac/busi/catalog.save")	public Response save(CamacBusiCatalog camacBusiCatalog) throws ControllerException {		camacBusiCatalogService.save(camacBusiCatalog);		return Response.success(camacBusiCatalog.getCatalogId());	}		@Description("CAMAC_产品目录-删除")	@RequestMapping(value="camac/busi/catalog.delete")	public Response delete(String catalogId) throws ControllerException {		if(StringUtils.isEmpty(catalogId))			return Response.error("catalogId不能为空");		camacBusiCatalogService.deleteOne(catalogId);		return Response.success();	}	@Description("CAMAC_产品目录-根据主键查询")	@RequestMapping(value="camac/busi/catalog.one")	public Response one(String catalogId) throws ControllerException {		if(StringUtils.isEmpty(catalogId))			return Response.error("catalogId不能为空");		CamacBusiCatalog camacBusiCatalog = camacBusiCatalogService.queryOne(catalogId);		return Response.success(camacBusiCatalog);	}		@Description("CAMAC_产品目录-查询列表")	@RequestMapping(value="camac/busi/catalog.list")	public Response list(@RequestParam Map<String, Object> map) throws ControllerException {		boolean pageOK = PageUtils.isOK(map);				if(!pageOK)			return Response.error("分页参数错误");				long total = camacBusiCatalogService.queryMapListCount(map);				Page page = PageUtils.initPage(map, total);				List<Map<String,Object>> list = camacBusiCatalogService.queryMapList(map);		return Response.success(list, page);	}		@Description("CAMAC_产品目录-根据主键修改")	@RequestMapping(value="camac/busi/catalog.update")	public Response update(@RequestParam Map<String, Object> map) throws ControllerException {		if(MapUtils.isEmpty(map, "catalogId"))			return Response.error("catalogId不能为空");		camacBusiCatalogService.updateOne(map);		return Response.success();	}}