package cn.bjjoy.bms.setting.controller;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Description;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.CrossOrigin;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import cn.bjjoy.bms.exception.ControllerException;import cn.bjjoy.bms.setting.entity.CamacBusiCatalogProduct;import cn.bjjoy.bms.setting.service.CamacBusiCatalogProductService;import cn.bjjoy.bms.util.MapUtils;import cn.bjjoy.bms.util.Page;import cn.bjjoy.bms.util.PageUtils;import cn.bjjoy.bms.util.Response;import cn.bjjoy.bms.util.StringUtils;/** * 类描述   : CAMAC_产品 * 创建人	：system * 创建时间 ：2018-09-12 16:16:56 * @version 1.0 */@Controller@CrossOriginpublic class CamacBusiCatalogProductController extends BaseController {	@Autowired	private CamacBusiCatalogProductService camacBusiCatalogProductService;		@Description("CAMAC_产品-添加")	@RequestMapping(value="camac/busi/catalog/product.save")	public Response save(CamacBusiCatalogProduct camacBusiCatalogProduct) throws ControllerException {		camacBusiCatalogProductService.save(camacBusiCatalogProduct);		return Response.success(camacBusiCatalogProduct.getProductId());	}		@Description("CAMAC_产品-删除")	@RequestMapping(value="camac/busi/catalog/product.delete")	public Response delete(String productId) throws ControllerException {		if(StringUtils.isEmpty(productId))			return Response.error("productId不能为空");		camacBusiCatalogProductService.deleteOne(productId);		return Response.success();	}	@Description("CAMAC_产品-根据主键查询")	@RequestMapping(value="camac/busi/catalog/product.one")	public Response one(String productId) throws ControllerException {		if(StringUtils.isEmpty(productId))			return Response.error("productId不能为空");		CamacBusiCatalogProduct camacBusiCatalogProduct = camacBusiCatalogProductService.queryOne(productId);		return Response.success(camacBusiCatalogProduct);	}		@Description("CAMAC_产品-查询列表")	@RequestMapping(value="camac/busi/catalog/product.list")	public Response list(@RequestParam Map<String, Object> map) throws ControllerException {		boolean pageOK = PageUtils.isOK(map);				if(!pageOK)			return Response.error("分页参数错误");				long total = camacBusiCatalogProductService.queryMapListCount(map);				Page page = PageUtils.initPage(map, total);				List<Map<String,Object>> list = camacBusiCatalogProductService.queryMapList(map);		return Response.success(list, page);	}		@Description("CAMAC_产品-根据主键修改")	@RequestMapping(value="camac/busi/catalog/product.update")	public Response update(@RequestParam Map<String, Object> map) throws ControllerException {		if(MapUtils.isEmpty(map, "productId"))			return Response.error("productId不能为空");		camacBusiCatalogProductService.updateOne(map);		return Response.success();	}}