package cn.bjjoy.bms.setting.controller;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Description;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.CrossOrigin;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import cn.bjjoy.bms.exception.ControllerException;import cn.bjjoy.bms.setting.entity.CamacBusiChannelKeymsg;import cn.bjjoy.bms.setting.service.CamacBusiChannelKeymsgService;import cn.bjjoy.bms.util.MapUtils;import cn.bjjoy.bms.util.Page;import cn.bjjoy.bms.util.PageUtils;import cn.bjjoy.bms.util.Response;import cn.bjjoy.bms.util.StringUtils;/** * 类描述   : CAMAC_KeyMessage * 创建人	：system * 创建时间 ：2018-09-12 16:16:56 * @version 1.0 */@Controller@CrossOriginpublic class CamacBusiChannelKeymsgController extends BaseController {	@Autowired	private CamacBusiChannelKeymsgService camacBusiChannelKeymsgService;		@Description("CAMAC_KeyMessage-添加")	@RequestMapping(value="camac/busi/channel/keymsg.save")	public Response save(CamacBusiChannelKeymsg camacBusiChannelKeymsg) throws ControllerException {		camacBusiChannelKeymsgService.save(camacBusiChannelKeymsg);		return Response.success(camacBusiChannelKeymsg.getKeymsgId());	}		@Description("CAMAC_KeyMessage-删除")	@RequestMapping(value="camac/busi/channel/keymsg.delete")	public Response delete(String keymsgId) throws ControllerException {		if(StringUtils.isEmpty(keymsgId))			return Response.error("keymsgId不能为空");		camacBusiChannelKeymsgService.deleteOne(keymsgId);		return Response.success();	}	@Description("CAMAC_KeyMessage-根据主键查询")	@RequestMapping(value="camac/busi/channel/keymsg.one")	public Response one(String keymsgId) throws ControllerException {		if(StringUtils.isEmpty(keymsgId))			return Response.error("keymsgId不能为空");		CamacBusiChannelKeymsg camacBusiChannelKeymsg = camacBusiChannelKeymsgService.queryOne(keymsgId);		return Response.success(camacBusiChannelKeymsg);	}		@Description("CAMAC_KeyMessage-查询列表")	@RequestMapping(value="camac/busi/channel/keymsg.list")	public Response list(@RequestParam Map<String, Object> map) throws ControllerException {		boolean pageOK = PageUtils.isOK(map);				if(!pageOK)			return Response.error("分页参数错误");				long total = camacBusiChannelKeymsgService.queryMapListCount(map);				Page page = PageUtils.initPage(map, total);				List<Map<String,Object>> list = camacBusiChannelKeymsgService.queryMapList(map);		return Response.success(list, page);	}		@Description("CAMAC_KeyMessage-根据主键修改")	@RequestMapping(value="camac/busi/channel/keymsg.update")	public Response update(@RequestParam Map<String, Object> map) throws ControllerException {		if(MapUtils.isEmpty(map, "keymsgId"))			return Response.error("keymsgId不能为空");		camacBusiChannelKeymsgService.updateOne(map);		return Response.success();	}}