package cn.bjjoy.bms.setting.controller;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Description;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.CrossOrigin;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import cn.bjjoy.bms.exception.ControllerException;import cn.bjjoy.bms.setting.entity.CamacBusiKeymsgFeedbk;import cn.bjjoy.bms.setting.service.CamacBusiKeymsgFeedbkService;import cn.bjjoy.bms.util.MapUtils;import cn.bjjoy.bms.util.Page;import cn.bjjoy.bms.util.PageUtils;import cn.bjjoy.bms.util.Response;import cn.bjjoy.bms.util.StringUtils;/** * 类描述   : CAMAC_KeyMessage反馈 * 创建人	：system * 创建时间 ：2018-09-12 16:16:56 * @version 1.0 */@Controller@CrossOriginpublic class CamacBusiKeymsgFeedbkController extends BaseController {	@Autowired	private CamacBusiKeymsgFeedbkService camacBusiKeymsgFeedbkService;		@Description("CAMAC_KeyMessage反馈-添加")	@RequestMapping(value="camac/busi/keymsg/feedbk.save")	public Response save(CamacBusiKeymsgFeedbk camacBusiKeymsgFeedbk) throws ControllerException {		camacBusiKeymsgFeedbkService.save(camacBusiKeymsgFeedbk);		return Response.success(camacBusiKeymsgFeedbk.getFeedbkId());	}		@Description("CAMAC_KeyMessage反馈-删除")	@RequestMapping(value="camac/busi/keymsg/feedbk.delete")	public Response delete(String feedbkId) throws ControllerException {		if(StringUtils.isEmpty(feedbkId))			return Response.error("feedbkId不能为空");		camacBusiKeymsgFeedbkService.deleteOne(feedbkId);		return Response.success();	}	@Description("CAMAC_KeyMessage反馈-根据主键查询")	@RequestMapping(value="camac/busi/keymsg/feedbk.one")	public Response one(String feedbkId) throws ControllerException {		if(StringUtils.isEmpty(feedbkId))			return Response.error("feedbkId不能为空");		CamacBusiKeymsgFeedbk camacBusiKeymsgFeedbk = camacBusiKeymsgFeedbkService.queryOne(feedbkId);		return Response.success(camacBusiKeymsgFeedbk);	}		@Description("CAMAC_KeyMessage反馈-查询列表")	@RequestMapping(value="camac/busi/keymsg/feedbk.list")	public Response list(@RequestParam Map<String, Object> map) throws ControllerException {		boolean pageOK = PageUtils.isOK(map);				if(!pageOK)			return Response.error("分页参数错误");				long total = camacBusiKeymsgFeedbkService.queryMapListCount(map);				Page page = PageUtils.initPage(map, total);				List<Map<String,Object>> list = camacBusiKeymsgFeedbkService.queryMapList(map);		return Response.success(list, page);	}		@Description("CAMAC_KeyMessage反馈-根据主键修改")	@RequestMapping(value="camac/busi/keymsg/feedbk.update")	public Response update(@RequestParam Map<String, Object> map) throws ControllerException {		if(MapUtils.isEmpty(map, "feedbkId"))			return Response.error("feedbkId不能为空");		camacBusiKeymsgFeedbkService.updateOne(map);		return Response.success();	}}