package cn.bjjoy.bms.setting.controller;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Description;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.CrossOrigin;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.ResponseBody;import cn.bjjoy.bms.exception.ControllerException;import cn.bjjoy.bms.setting.entity.CamacQuestion;import cn.bjjoy.bms.setting.service.CamacQuestionService;import cn.bjjoy.bms.util.MapUtils;import cn.bjjoy.bms.util.Page;import cn.bjjoy.bms.util.PageUtils;import cn.bjjoy.bms.util.Response;import cn.bjjoy.bms.util.StringUtils;/** * 类描述   : CAMAC_角色 * 创建人	：system * 创建时间 ：2018-09-12 16:16:57 * @version 1.0 */@Controller@CrossOrigin@RequestMapping(value="question")public class CamacQuestionController extends BaseController {	@Autowired	private CamacQuestionService camacQuestionService;		@Description("CAMAC_角色-添加")	@RequestMapping(value="save")	public Response save(CamacQuestion camacQuestion) throws ControllerException {		camacQuestionService.save(camacQuestion);		return Response.success(camacQuestion.getQuestionId());	}		@Description("CAMAC_角色-删除")	@RequestMapping(value="delete")	public Response delete(String questionId) throws ControllerException {		if(StringUtils.isEmpty(questionId))			return Response.error("questionId不能为空");		camacQuestionService.deleteOne(questionId);		return Response.success();	}	@Description("CAMAC_角色-根据主键查询")	@RequestMapping(value="one")	public Response one(String questionId) throws ControllerException {		if(StringUtils.isEmpty(questionId))			return Response.error("questionId不能为空");		CamacQuestion camacQuestion = camacQuestionService.queryOne(questionId);		return Response.success(camacQuestion);	}		@Description("CAMAC_角色-查询列表")	@RequestMapping(value="list")	@ResponseBody	public Response list(@RequestParam Map<String, Object> map) throws ControllerException {		boolean pageOK = PageUtils.isOK(map);				if(!pageOK)			return Response.error("分页参数错误");				long total = camacQuestionService.queryMapListCount(map);				Page page = PageUtils.initPage(map, total);				List<Map<String,Object>> list = camacQuestionService.queryMapList(map);		return Response.success(list, page);	}		@Description("CAMAC_角色-根据主键修改")	@RequestMapping(value="update")	public Response update(@RequestParam Map<String, Object> map) throws ControllerException {		if(MapUtils.isEmpty(map, "questionId"))			return Response.error("questionId不能为空");		camacQuestionService.updateOne(map);		return Response.success();	}}