package cn.bjjoy.bms.setting.controller;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Description;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.CrossOrigin;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import cn.bjjoy.bms.exception.ControllerException;import cn.bjjoy.bms.setting.entity.CamacRole;import cn.bjjoy.bms.setting.service.CamacRoleService;import cn.bjjoy.bms.util.MapUtils;import cn.bjjoy.bms.util.Page;import cn.bjjoy.bms.util.PageUtils;import cn.bjjoy.bms.util.Response;import cn.bjjoy.bms.util.StringUtils;/** * 类描述   : CAMAC_角色 * 创建人	：system * 创建时间 ：2018-09-12 16:16:57 * @version 1.0 */@Controller@CrossOriginpublic class CamacRoleController extends BaseController {	@Autowired	private CamacRoleService camacRoleService;		@Description("CAMAC_角色-添加")	@RequestMapping(value="camac/role.save")	public Response save(CamacRole camacRole) throws ControllerException {		camacRoleService.save(camacRole);		return Response.success(camacRole.getId());	}		@Description("CAMAC_角色-删除")	@RequestMapping(value="camac/role.delete")	public Response delete(String id) throws ControllerException {		if(StringUtils.isEmpty(id))			return Response.error("id不能为空");		camacRoleService.deleteOne(id);		return Response.success();	}	@Description("CAMAC_角色-根据主键查询")	@RequestMapping(value="camac/role.one")	public Response one(String id) throws ControllerException {		if(StringUtils.isEmpty(id))			return Response.error("id不能为空");		CamacRole camacRole = camacRoleService.queryOne(id);		return Response.success(camacRole);	}		@Description("CAMAC_角色-查询列表")	@RequestMapping(value="camac/role.list")	public Response list(@RequestParam Map<String, Object> map) throws ControllerException {		boolean pageOK = PageUtils.isOK(map);				if(!pageOK)			return Response.error("分页参数错误");				long total = camacRoleService.queryMapListCount(map);				Page page = PageUtils.initPage(map, total);				List<Map<String,Object>> list = camacRoleService.queryMapList(map);		return Response.success(list, page);	}		@Description("CAMAC_角色-根据主键修改")	@RequestMapping(value="camac/role.update")	public Response update(@RequestParam Map<String, Object> map) throws ControllerException {		if(MapUtils.isEmpty(map, "id"))			return Response.error("id不能为空");		camacRoleService.updateOne(map);		return Response.success();	}}