package cn.bjjoy.bms.setting.controller;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Description;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.CrossOrigin;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import cn.bjjoy.bms.exception.ControllerException;import cn.bjjoy.bms.setting.entity.CamacUser;import cn.bjjoy.bms.setting.service.CamacUserService;import cn.bjjoy.bms.util.MapUtils;import cn.bjjoy.bms.util.Page;import cn.bjjoy.bms.util.PageUtils;import cn.bjjoy.bms.util.Response;import cn.bjjoy.bms.util.StringUtils;/** * 类描述   : CAMAC_用户 * 创建人	：system * 创建时间 ：2018-09-12 16:16:57 * @version 1.0 */@Controller@CrossOriginpublic class CamacUserController extends BaseController {	@Autowired	private CamacUserService camacUserService;		@Description("CAMAC_用户-添加")	@RequestMapping(value="camac/user.save")	public Response save(CamacUser camacUser) throws ControllerException {		camacUserService.save(camacUser);		return Response.success(camacUser.getUserId());	}		@Description("CAMAC_用户-删除")	@RequestMapping(value="camac/user.delete")	public Response delete(String userId) throws ControllerException {		if(StringUtils.isEmpty(userId))			return Response.error("userId不能为空");		camacUserService.deleteOne(userId);		return Response.success();	}	@Description("CAMAC_用户-根据主键查询")	@RequestMapping(value="camac/user.one")	public Response one(String userId) throws ControllerException {		if(StringUtils.isEmpty(userId))			return Response.error("userId不能为空");		CamacUser camacUser = camacUserService.queryOne(userId);		return Response.success(camacUser);	}		@Description("CAMAC_用户-查询列表")	@RequestMapping(value="camac/user.list")	public Response list(@RequestParam Map<String, Object> map) throws ControllerException {		boolean pageOK = PageUtils.isOK(map);				if(!pageOK)			return Response.error("分页参数错误");				long total = camacUserService.queryMapListCount(map);				Page page = PageUtils.initPage(map, total);				List<Map<String,Object>> list = camacUserService.queryMapList(map);		return Response.success(list, page);	}		@Description("CAMAC_用户-根据主键修改")	@RequestMapping(value="camac/user.update")	public Response update(@RequestParam Map<String, Object> map) throws ControllerException {		if(MapUtils.isEmpty(map, "userId"))			return Response.error("userId不能为空");		camacUserService.updateOne(map);		return Response.success();	}}