package cn.bjjoy.bms.setting.controller;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Description;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.CrossOrigin;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import cn.bjjoy.bms.exception.ControllerException;import cn.bjjoy.bms.setting.entity.CamacVoiceNews;import cn.bjjoy.bms.setting.service.CamacVoiceNewsService;import cn.bjjoy.bms.util.MapUtils;import cn.bjjoy.bms.util.Page;import cn.bjjoy.bms.util.PageUtils;import cn.bjjoy.bms.util.Response;import cn.bjjoy.bms.util.StringUtils;/** * 类描述   : CAMAC_新闻 * 创建人	：system * 创建时间 ：2018-09-12 16:16:57 * @version 1.0 */@Controller@CrossOriginpublic class CamacVoiceNewsController extends BaseController {	@Autowired	private CamacVoiceNewsService camacVoiceNewsService;		@Description("CAMAC_新闻-添加")	@RequestMapping(value="camac/voice/news.save")	public Response save(CamacVoiceNews camacVoiceNews) throws ControllerException {		camacVoiceNewsService.save(camacVoiceNews);		return Response.success(camacVoiceNews.getNewsId());	}		@Description("CAMAC_新闻-删除")	@RequestMapping(value="camac/voice/news.delete")	public Response delete(String newsId) throws ControllerException {		if(StringUtils.isEmpty(newsId))			return Response.error("newsId不能为空");		camacVoiceNewsService.deleteOne(newsId);		return Response.success();	}	@Description("CAMAC_新闻-根据主键查询")	@RequestMapping(value="camac/voice/news.one")	public Response one(String newsId) throws ControllerException {		if(StringUtils.isEmpty(newsId))			return Response.error("newsId不能为空");		CamacVoiceNews camacVoiceNews = camacVoiceNewsService.queryOne(newsId);		return Response.success(camacVoiceNews);	}		@Description("CAMAC_新闻-查询列表")	@RequestMapping(value="camac/voice/news.list")	public Response list(@RequestParam Map<String, Object> map) throws ControllerException {		boolean pageOK = PageUtils.isOK(map);				if(!pageOK)			return Response.error("分页参数错误");				long total = camacVoiceNewsService.queryMapListCount(map);				Page page = PageUtils.initPage(map, total);				List<Map<String,Object>> list = camacVoiceNewsService.queryMapList(map);		return Response.success(list, page);	}		@Description("CAMAC_新闻-根据主键修改")	@RequestMapping(value="camac/voice/news.update")	public Response update(@RequestParam Map<String, Object> map) throws ControllerException {		if(MapUtils.isEmpty(map, "newsId"))			return Response.error("newsId不能为空");		camacVoiceNewsService.updateOne(map);		return Response.success();	}}