package cn.bjjoy.bms.setting.controller;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Description;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import cn.bjjoy.bms.exception.ControllerException;import cn.bjjoy.bms.setting.persist.model.Maintainrecord;import cn.bjjoy.bms.setting.service.MaintainrecordService;import cn.bjjoy.bms.util.MapUtils;import cn.bjjoy.bms.util.Page;import cn.bjjoy.bms.util.PageUtils;import cn.bjjoy.bms.util.Response;import cn.bjjoy.bms.util.StringUtils;/** * 类描述   :  * 创建人	：system * 创建时间 ：2018-09-13 23:02:18 * @version 1.0 */@RestControllerpublic class MaintainrecordController extends BaseController {	@Autowired	private MaintainrecordService maintainrecordService;		@Description("-添加")	@RequestMapping(value="maintainrecord.save")	public Response save(Maintainrecord maintainrecord) throws ControllerException {		maintainrecordService.save(maintainrecord);		return Response.success(maintainrecord.getId());	}		@Description("-删除")	@RequestMapping(value="maintainrecord.delete")	public Response delete(String id) throws ControllerException {		if(StringUtils.isEmpty(id))			return Response.error("id不能为空");		maintainrecordService.deleteOne(id);		return Response.success();	}	@Description("-根据主键查询")	@RequestMapping(value="maintainrecord.one")	public Response one(String id) throws ControllerException {		if(StringUtils.isEmpty(id))			return Response.error("id不能为空");		Maintainrecord maintainrecord = maintainrecordService.queryOne(id);		return Response.success(maintainrecord);	}		@Description("-查询列表")	@RequestMapping(value="maintainrecord.list")	public Response list(@RequestParam Map<String, Object> map) throws ControllerException {		boolean pageOK = PageUtils.isOK(map);				if(!pageOK)			return Response.error("分页参数错误");				long total = maintainrecordService.queryMapListCount(map);				Page page = PageUtils.initPage(map, total);				List<Map<String,Object>> list = maintainrecordService.queryMapList(map);		return Response.success(list, page);	}		@Description("-根据主键修改")	@RequestMapping(value="maintainrecord.update")	public Response update(@RequestParam Map<String, Object> map) throws ControllerException {		if(MapUtils.isEmpty(map, "id"))			return Response.error("id不能为空");		maintainrecordService.updateOne(map);		return Response.success();	}}