package cn.bjjoy.bms.setting.service.impl;import java.util.ArrayList;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import cn.bjjoy.bms.setting.dao.BaseDao;import cn.bjjoy.bms.setting.persist.mapper.DatacardDao;import cn.bjjoy.bms.setting.persist.model.CostStandard;import cn.bjjoy.bms.setting.persist.model.Datacard;import cn.bjjoy.bms.setting.service.DatacardService;/** * 类描述   :  * 创建人	：system * 创建时间 ：2018-09-13 23:02:18 * @version 1.0 */@Service@Transactional@SuppressWarnings("rawtypes")public class DatacardServiceImpl extends BaseServiceImpl<Datacard> implements DatacardService {		String[] costs = {"5元包月","10元包月","20元包月","30元包月","50元包月","100元包月","200元包月"} ;	@Autowired	private DatacardDao datacardDao;	@Override	public BaseDao<Datacard> getBaseDao() {		return datacardDao;	}	@Override	public void updateById(Map map) {		datacardDao.updateDatacardById(map);	}		public List<CostStandard> getCostStandard(){				List<CostStandard> costStandards = new ArrayList<>() ;		for (int i = 0; i < costs.length; i++) {			costStandards.add(new CostStandard(String.valueOf(i + 1), costs[i]));		}		return costStandards ;	}		public String getCostStandards(String standards){		return costs[Integer.parseInt(standards) - 1] ;	}			@Override	public List<Datacard> convertStandardAndStatus(List<Datacard> datacards) {		List<Datacard> finalDatacards = new ArrayList<>();		for(Datacard card : datacards){			card.setCostStandard(getCostStandards(card.getCostStandard()));			card.setCurrentState("1".equals(card.getCurrentState()) ? "正常" : "异常");			finalDatacards.add(card);		}		return finalDatacards;	}	}