package cn.bjjoy.bms.setting.service.impl;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import cn.bjjoy.bms.setting.dao.BaseDao;import cn.bjjoy.bms.setting.persist.mapper.EquipdataDao;import cn.bjjoy.bms.setting.persist.model.Equipdata;import cn.bjjoy.bms.setting.service.EquipdataService;/** * 类描述   :  * 创建人	：system * 创建时间 ：2018-09-13 23:02:18 * @version 1.0 */@Service@Transactionalpublic class EquipdataServiceImpl extends BaseServiceImpl<Equipdata> implements EquipdataService {		@Autowired	private EquipdataDao equipdataDao;	@Override	public BaseDao<Equipdata> getBaseDao() {		return equipdataDao;	}	public long getCurrentAccount(){		return equipdataDao.getNewestDataCount() ;	}		@Override	public List<Map<String, Object>> queryOne(Map map) {		return equipdataDao.getNewestData(map);	}		@Override	public long getHistoryAccountNoPage(){		return equipdataDao.getDataNoPageCount() ;	}		@Override	public List<Map<String, Object>> getHistoryNoPage(Map map) {		return equipdataDao.getDataNoPage(map);	}		}