package cn.bjjoy.bms.setting.service.impl;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import cn.bjjoy.bms.setting.dao.BaseDao;import cn.bjjoy.bms.setting.persist.mapper.EquiptypeDao;import cn.bjjoy.bms.setting.persist.model.Equiptype;import cn.bjjoy.bms.setting.service.EquiptypeService;/** * 类描述   :  * 创建人	：system * 创建时间 ：2018-09-13 23:02:18 * @version 1.0 */@Service@Transactionalpublic class EquiptypeServiceImpl extends BaseServiceImpl<Equiptype> implements EquiptypeService {		@Autowired	private EquiptypeDao equiptypeDao;	@Override	public BaseDao<Equiptype> getBaseDao() {		return equiptypeDao;	}		public List<Map<String,Object>> getListNoPage(Map map ){		return equiptypeDao.queryMapListNoPage(map) ;	}	@Override	public List<Map<String, Object>> getSubType(Map map) {		return equiptypeDao.querySubTypes(map) ;	}		public List<Integer> getSubTypeIds(int parentId){		List<Integer> result = new ArrayList<Integer>();		Map map = new HashMap<String,Object>();		map.put("parentId", parentId) ;		List<Map<String, Object>> equipTypes = equiptypeDao.getSubTypes(map) ;		for(Map equipMap : equipTypes){			result.add((Integer) equipMap.get("id")) ;		}		return result;	}				}