mapper.select:
SELECT equiptype.id AS id, equiptype.parent_id AS parentId, equiptype.name AS name, equiptype.add_per AS addPer, equiptype.add_time AS addTime, equiptype.type_layer AS typeLayer, equiptype.addressCode AS addressCode, equiptype.system_id AS systemId, equiptype.longitude AS longitude, equiptype.latitude AS latitude, equiptype.IP AS IP, equiptype.Zguanli_per AS ZguanliPer, equiptype.Zguanli_phone AS ZguanliPhone, equiptype.Cguanli_per AS CguanliPer, equiptype.Cguanli_phone AS CguanliPhone, equiptype.Jguanli_per AS JguanliPer, equiptype.Jguanli_phone AS JguanliPhone, equiptype.bengxing AS bengxing, equiptype.koujing AS koujing, equiptype.gonglv AS gonglv, equiptype.xinhao AS xinhao FROM equiptype where 1 = 1
AND equiptype.id IN
#{item}
AND equiptype.system_id = ${systemId}
and (instr(equiptype.name ,#{stationName}) or instr(equiptype.addressCode,#{stationName}))
order by equiptype.id asc

SELECT count(1) FROM equiptype where 1 = 1
AND equiptype.id IN
#{item}
AND equiptype.system_id = #{systemId}
and (instr(equiptype.name ,#{stationName}) or instr(equiptype.addressCode,#{stationName}))

SELECT equiptype.id AS id, equiptype.parent_id AS parentId, equiptype.name AS name, equiptype.add_per AS addPer, equiptype.add_time AS addTime, equiptype.type_layer AS typeLayer, equiptype.addressCode AS addressCode, equiptype.system_id AS systemId, equiptype.longitude AS longitude, equiptype.latitude AS latitude, equiptype.IP AS IP, equiptype.Zguanli_per AS zguanliPer, equiptype.Zguanli_phone AS zguanliPhone, equiptype.Cguanli_per AS cguanliPer, equiptype.Cguanli_phone AS cguanliPhone, equiptype.Jguanli_per AS jguanliPer, equiptype.Jguanli_phone AS jguanliPhone, equiptype.bengxing AS bengxing, equiptype.koujing AS koujing, equiptype.gonglv AS gonglv, equiptype.xinhao AS xinhao, equiptype.tag AS tag FROM equiptype WHERE 1 = 1
AND equiptype.id IN
#{item}
and parent_id = #{parentId}
AND equiptype.system_id = #{systemId}

select id from equiptype where id = #{parentId}

	 and (instr(name ,#{stationName}) or instr(addressCode,#{stationName})) 


	AND system_id = #{systemId} 

UNION all 
select id from equiptype where parent_id = #{parentId}

	 and (instr(name ,#{stationName}) or instr( addressCode,#{stationName})) 


	AND system_id = #{systemId} 

UNION all 
select id from equiptype where parent_id in(select id from equiptype where parent_id = #{parentId})

	 and (instr(name ,#{stationName}) or instr( addressCode,#{stationName})) 


	AND system_id = #{systemId} 

UNION all 
select id from equiptype where parent_id in(select id from equiptype where parent_id in(select id from equiptype where parent_id = #{parentId}))

	 and (instr(name ,#{stationName}) or instr( addressCode,#{stationName})) 


	AND system_id = #{systemId} 

UNION all 
select id from equiptype where parent_id in(select id from equiptype 
where parent_id in(select id from equiptype where parent_id  in(select id from equiptype where parent_id = #{parentId}))) 

	 and (instr(name ,#{stationName}) or instr( addressCode,#{stationName})) 


	AND system_id = #{systemId}

SELECT equiptype.addressCode AS addressCode FROM equiptype WHERE 1 = 1
and equiptype.system_id = #{systemId}
and equiptype.type_layer = 4
